import React, { Component } from 'react';
class Coupon extends Component {
    onCouponUse = (event) => {
        this.props.setCoupon();
    }
    render() {
        const status = this.props.data.status;
        const coupon = this.props.data.secretWord;
        return (
            <div className="col-sm-6 mt-2 mx-auto">
                <div className="card">
                    <div className="card-header text-center">
                        <button className="btn btn-success btn-sm" disable={!status} onClick={this.onCouponUse}>ใช้คูปอง</button>
                    </div>
                    <div className="card-body">
                        <span className="mr-2">รหัสคูปอง</span>
                        <span className="badge badge-secondary">{status?soupon:'คูปองถูกใช้ไปแล้ว'}</span>
                    </div>
                </div>
            </div>
        )
    }
}

export default Coupon;
